mapper.select:
select u.user_id, u.user_type, u.dept_id, u.enterprise_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from sys_user u
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'

	AND u.user_id = #{userId}


	AND u.user_type = #{userType}


	AND u.enterprise_id = #{enterpriseId}


	AND u.user_name like concat('%', #{userName}, '%')


	AND u.status = #{status}


	AND u.phonenumber like concat('%', #{phonenumber}, '%')


	AND to_timestamp(to_char(u.create_time,'YYYY-mm-dd'), 'YYYY-mm-dd') >= to_timestamp(#{params.beginTime}, 'YYYY-mm-dd')


	AND to_timestamp(to_char(u.create_time,'YYYY-mm-dd'), 'YYYY-mm-dd') <= to_timestamp(#{params.endTime}, 'YYYY-mm-dd')


	AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE cast(#{deptId} as varchar) = any(string_to_array(ancestors,',')) ))

select distinct u.user_id, u.user_type, u.enterprise_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')

mapper.update:
update sys_user
enterprise_id = #{enterpriseId}, dept_id = #{deptId}, user_type = #{userType}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, is_first = #{isFirst}, google = #{google}, update_time = now()
where user_id = #{userId}

update sys_user
password = #{password}, update_time = now()
where email = #{email}
